mapper.select:
SELECT count(id) FROM qqbs_sale_window 
WHERE 1=1 
	AND name like #{name}
	AND type=#{type} 
	AND is_display=#{isDisplay} 
	AND t1.start_time > concat(#{startTime},  ' 00:00:01') AND t1.start_time < concat(#{startTime},  ' 23:59:59') 
	AND start_time > now() 
	AND start_time < now() AND end_time > now() 
	AND end_time < now() 
	AND `name` like #{saleName}

       	SELECT 
       		count(ss.id)
		FROM
			qqbs_sale_window sw,
			seller_settlement ss,
			seller s
		WHERE
			sw.id = ss.sale_window_id
			AND ss.seller_id = s.id
			and ss.sale_window_id=#{saleWindowId}
	        and sw.type=#{saleWindowType}
	        and sw.`name` like #{saleWindowName}
	        and ss.is_deal_with=#{isDealWith}
	        and s.id=#{sellerId}

		SELECT
			COUNT(t1.id)
		FROM
			qqbs_sale_window t1
		LEFT OUTER JOIN product t2 ON t1.display_id = t2.id
		WHERE t1.type = 1
      	AND t1.id=#{id}
      	AND t1.name like #{name}
      	AND t1.is_display=#{isDisplay} 
      	AND t1.start_time > concat(#{startTime},  ' 00:00:01') AND t1.start_time < concat(#{startTime},  ' 23:59:59') 
      	AND t1.start_time > now() 
      	AND t1.start_time < now() AND t1.end_time > now() 
      	AND t1.end_time < now() 
      	AND t2.id = #{productId} 
      	AND t2.id = #{pId} 
      	AND t2.brand_id = #{brandId} 
      	AND t2.`name` like #{productName} 		
		AND t2.seller_id = #{sellerId}

		SELECT
			COUNT(DISTINCT(t1.id))
		FROM
			qqbs_sale_window t1
		LEFT OUTER JOIN relation_activities_common_and_product t2 ON t1.display_id = t2.activities_common_id
		LEFT OUTER JOIN product t3 ON t2.product_id = t3.id
		WHERE
			t1.type = 2
		AND t1.id=#{id}
      	AND t1.name like #{name}
      	AND t1.is_display=#{isDisplay} 
      	AND t1.start_time > concat(#{startTime},  ' 00:00:01') AND t1.start_time < concat(#{startTime},  ' 23:59:59')  
      	AND t1.start_time > now() 
      	AND t1.start_time < now() AND t1.end_time > now() 
      	AND t1.end_time < now() 
      	AND t2.activities_common_id = #{productId} 
      	AND t3.id = #{pId} 
      	AND t3.brand_id = #{brandId} 
      	AND t3.`name` like #{productName} 
		AND t3.seller_id = #{sellerId}

		SELECT
			COUNT(t1.id)
		FROM
			qqbs_sale_window t1
		WHERE 1 = 1
      	AND t1.id=#{id}
      	AND t1.name like #{name}
      	AND t1.is_display=#{isDisplay} 
      	AND t1.start_time > concat(#{startTime},  ' 00:00:01') AND t1.start_time < concat(#{startTime},  ' 23:59:59') 
      	AND t1.start_time > now() 
      	AND t1.start_time < now() AND t1.end_time > now() 
      	AND t1.end_time < now() 
      	AND t2.brand_id = #{brandId} 
      	AND t2.`name` like #{productName} 		
		AND t2.seller_id = #{sellerId}
