mapper.update:
UPDATE `activities_common`
name=#{name}, `desc`=#{desc}, `gegesay`=#{gegesay}, `wx_share_title`=#{wxShareTitle}, image=#{image}, is_available=#{isAvailable}, gege_image_id=#{gegeImageId}, new_image17=#{newImage17}, type=#{type}
where id=#{id}

mapper.select:
SELECT
t1.*
FROM
activities_common t1
LEFT OUTER JOIN
relation_activities_common_and_product t2 ON t1.id
=
t2.activities_common_id
LEFT OUTER JOIN product t3 ON t2.product_id =
t3.id
WHERE
1=1
AND t1.name like #{name} 
AND t1.is_available=#{isAvailable} 
 AND t1.id=#{id} 
 AND t2.product_id=#{productId} 
 AND t3.`name` like #{productName} 
 AND t3.`code` like #{productCode} 
 AND t1.`type`=#{type} 
GROUP BY t1.id
ORDER BY id DESC
LIMIT #{start},#{max}

SELECT
count(DISTINCT(t1.id))
FROM
activities_common t1
LEFT OUTER JOIN
relation_activities_common_and_product t2 ON t1.id
=
t2.activities_common_id
LEFT OUTER JOIN product t3 ON t2.product_id =
t3.id
WHERE
1=1
AND t1.name like #{name} 
AND t1.is_available=#{isAvailable} 
 AND t1.id=#{id} 
 AND t2.product_id=#{productId} 
 AND t3.`name` like #{productName} 
 AND t1.`type`=#{type}

SELECT COUNT(id) FROM
relation_activities_common_and_product WHERE
activities_common_id=#{id}

SELECT max(`order`) FROM
relation_activities_common_and_product WHERE
activities_common_id=#{id}

